  {id:17, exist:0, x_id:2, y_id:3, growtime: 0,seed_yg:0, seed_rw:0, color:0, height: 0, width: 0, price: 0, hasseed: 0, haspollen: 0},
  {id:18, exist:0, x_id:3, y_id:3, growtime: 0,seed_yg:0, seed_rw:0, color:0, height: 0, width: 0, price: 0, hasseed: 0, haspollen: 0},
  {id:19, exist:0, x_id:4, y_id:3, growtime: 0,seed_yg:0, seed_rw:0, color:0, height: 0, width: 0, price: 0, hasseed: 0, haspollen: 0},
  {id:20, exist:0, x_id:0, y_id:4, growtime: 0,seed_yg:0, seed_rw:0, color:0, height: 0, width: 0, price: 0, hasseed: 0, haspollen: 0},
