class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      web3: null,
      accounts: null,
      username: null,
      userbalance: null,
      contract: null,
      contract2: null,
      sprouts: sprouts
    }
    this.handleClick = this.handleClick.bind(this);
  }//end constructor
  componentDidMount = async() =>  {
    try {
      const web3 = new Web3(Web3.givenProvider || 'ws://localhost:8545');
      const accounts = await web3.eth.getAccounts();
      const networkId = await web3.eth.net.getId();

      const deployNetwork = SproutContract.networks[networkId];
      const instance = new web3.eth.Contract(
        SproutContract.abi,
        deployNetwork.address
      )
      this.setState({
        web3: web3,
        accounts: accounts,
        contract: instance
      });
      /*Register here*/

      const { value: username } = await MySwal.fire({
        title: 'Enter your user name',
        input: 'text',
        showCancelButton: true,
        inputValidator: (value) => {
          if (!value) {
            return 'You need to write something!'
          }
        }
      })
      if (username) {
        MySwal.fire(`Hello ${username}`);
        this.setState({username: username});
        console.log(this.state.username)
      }
      //end register
    } catch (error) {
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };//end componetdidmount
  //this is for contract testing
  async handleClick(){
    //var res = await this.state.contract.methods.SayHello().call({from: this.state.accounts[0]});
    //console.log(res)
    var res2 = await this.state.contract.methods.Register(this.state.accounts[0]).send({from: this.state.accounts[0]});
    console.log(res2)
  }
  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return(
      <div>
        <Header />
        <div id="main_container" class="ts container grid very">
          <div class="four wide column segment">
              <User username={this.state.username} userbalance={this.state.userbalance} />
          </div>
        </div>
      </div>
    )
  }
} 

ReactDOM.render(<App/>, document.getElementById('root'));
